mapper.select:
select u.uid, u.username, u.password, u.name, u.gender, u.phone, u.permission, u.money from user u where u.isDel = 0
and name like '%${user.name}%'
and gender = #{user.gender}
limit #{start}, #{pageCount}

select count(uid) from user where isDel = 0
and name like '%${name}%'
and gender = #{gender}
